clear all; %Limpa valores de execuções anteriores[arq caminho]=uigetfile('*.*','Selecione o arquivo de imagem'); %ABRE A INTERFACE DE CARREGAMENTOnomecompleto=strcat(caminho,arq); %CONCATENA STRINGS DE CAMINHOI = imread(nomecompleto); % Armazena a Imagem em formato RGB (Se arquivo válido: bmp, tiff...[M,N,C]=size(I); %Verifica o tamanho da imagem e plano de coresimshow (I); % Mostra a imagem capturadaImodificada = repmat(I,2);lin = 1;col = 1;linha = 1;while linha <= 2*M for coluna=1:2*N   if(mod(linha,2)==0)      if((lin+1) <=  M)        Imodificada(linha,coluna,1) = (I(lin,col,1)+I(lin+1, col+1, 1))/2;        Imodificada(linha,coluna,2) = (I(lin,col,2)+I(lin+1, col+1, 2))/2;        Imodificada(linha,coluna,3) = (I(lin,col,3)+I(lin+1, col+1, 3))/2;      endif    elseif(mod(coluna,2)==0)      if((col+1) <= N)        Imodificada(linha,coluna,1) = (I(lin,col,1)+I(lin, col+1, 1))/2;        Imodificada(linha,coluna,2) = (I(lin,col,2)+I(lin, col+1, 2))/2;        Imodificada(linha,coluna,3) = (I(lin,col,3)+I(lin, col+1, 3))/2;      endif    else      if(col <= N)         Imodificada(linha,coluna,1) = I(lin,col,1);        Imodificada(linha,coluna,2) = I(lin,col,2);        Imodificada(linha,coluna,3) = I(lin,col,3);      col++;      endif    endif  endfor  col = 1;  linha++;  if(mod(linha,2)!=0)    lin++;  endifendfigure(2);imshow(Imodificada);imwrite(Imodificada, 'imagemModificada.png');